import React from "react";
import QA from "./QA";

export default class Btn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      QAshown: false
    };
  }
  toogleQA = () => {
    this.setState(state => ({
      QAshown: !state.QAshown
    }));
    console.log(this.state);
  };
  render() {
    return (
      <>
        <button onClick={this.toogleQA}> </button>
        <div>
          <QA courseName={this.props.courseName} />
        </div>
      </>
    );
  }
}
