import React from "react";
import Btn from "./startLearnBtn";

//pass courseNames
function LeftGuide(props) {
  return (
    <>
      {props.courseNames.map((item, index) => {
        return (
          <button
            key={index}
            onClick={() => {
              props.Clickme({ item });
            }}
          >
            {item}
          </button>
        );
      })}
    </>
  );
}

function CourseDetail(props) {
  return (
    <div>
      <p>{props.introduction}</p>
    </div>
  );
}

export default class Gui extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      curCourse: "React"
    };
  }

  SetCurCourse = text => {
    this.setState({ curCourse: text });
  };

  render() {
    const title = this.props.data.map(item => item.name);
    const detail = this.props.data.map(item => item.detail);
    return (
      <>
        <LeftGuide courseNames={title} Clickme={this.SetCurCourse} />
        <CourseDetail introduction={detail[this.state.index]} />
      </>
    );
  }
}
