import React, { Component } from "react";
import { BrowserRouter, Link, Route, Switch, Redirect } from "react-router-dom";

function GuideBar() {
  return (
    <div>
      {/* 编写路由导航 */}
      <ul>
        <li>
          <Link to="/">Home Page</Link>
        </li>
        <li>
          <Link to="/classes">My Courses</Link>
        </li>
        <li>
          <Link to="/mine">My Account</Link>
        </li>
        <li>
          <Link to="/mineaaaa">Not Found</Link>
        </li>
      </ul>

      <Switch>
        <Route exact path="/" component={Home}></Route>
        <Route path="/classes" component={Classes}></Route>
        <Route path="/login" component={Login}></Route>
        <RouteGuard path="/mine" component={Mine}></RouteGuard>
        <Route path="/detail/:course" component={Detail}></Route>
        <Route component={Notfound}></Route>
      </Switch>
    </div>
  );
}

class RouteGuard extends Component {
  render() {
    const { component: Component, ...otherProps } = this.props;
    return (
      <Route
        {...otherProps}
        render={props =>
          auth.isLogin ? (
            <Component {...props}></Component>
          ) : (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: props.location.pathname }
              }}
            ></Redirect>
          )
        }
      ></Route>
    );
  }
}

//here you can write request to backend and change login State
const auth = {
  isLogin: false,
  login(callback) {
    this.isLogin = true;
    setTimeout(callback, 1000);
  }
};
class Login extends Component {
  state = {
    isLogin: false
  };
  login = () => {
    auth.login(() => {
      this.setState({
        isLogin: true
      });
    });
  };
  render() {
    const from =
      (this.props.location.state && this.props.location.state.from) || "/";
    if (this.state.isLogin) {
      return <Redirect to={from}></Redirect>;
    }
    return (
      <div>
        <p>Please Login Fist!</p>
        <button onClick={this.login}>Login</button>
      </div>
    );
  }
}

//Course Detail
function Detail({ match, location, history }) {
  console.log(match, location, history);
  console.log(location, "location");
  return (
    <div>
      This is Course {match.params.course}
      <button
        onClick={() => history.push({ pathname: "/", state: { foo: "bar" } })}
      >
        Go back to HomePage
      </button>
    </div>
  );
}

//Course
function Classes() {
  return (
    <div>
      <ul>
        <li>
          <Link to="/detail/react">java script</Link>
        </li>
        <li>
          <Link to="/detail/react">react</Link>
        </li>
        <li>
          <Link to="/detail/vue">redux</Link>
        </li>
      </ul>
    </div>
  );
}
//My Account Information
function Mine() {
  return (
    <div>
      <h2>My Account Info</h2>
      <ul>
        <li>
          <Link to="/mine/userinfo">My Account Number</Link>
        </li>
        <li>
          <Link to="/mine/password">My Password</Link>
        </li>
      </ul>
      {/* 路由配置 */}
      <Switch>
        <Route
          path="/mine/userinfo"
          component={() => <div>XXX@gmail.com</div>}
        ></Route>
        <Route
          path="/mine/password"
          component={() => <div>1234567</div>}
        ></Route>
        <Redirect to="/mine/userinfo"></Redirect>
      </Switch>
    </div>
  );
}
//Page not Found
function Notfound() {
  return <div>HTTP404 not found</div>;
}

export default function RouterSample() {
  return (
    <div>
      <BrowserRouter>
        <GuideBar />
      </BrowserRouter>
    </div>
  );
}
