import React from "react";
import QA from "./QA";

export default class Btn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      QAshown: false
    };
  }
  toogleQA = () => {
    this.setState(state => ({
      QAshown: !state.QAshown
    }));
  };
  render() {
    return (
      <>
        <button onClick={this.toogleQA}>show/hide QA</button>
        <QA courseName={this.props.courseName} style={{ display: "none" }} />
      </>
    );
  }
}
