import React from "react";
import QA from "./QA";
import favorBtn from "./addFavor";

class addQA extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      question: "",
      answer: ""
    };
  }
  questionChange = e => {
    this.setState({ question: e.target.value });
  };
  answerChange = e => {
    this.setState({ answer: e.target.value });
  };
  render() {
    return (
      <>
        <div
          style={{
            visibility: this.state.addShown ? "visible" : "hidden"
          }}
        >
          question <input onChange={this.questionChange} />
          answer
          <input onChange={this.answerChange} />
          <favorBtn />
        </div>
      </>
    );
  }
}

export default class Btn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      QAshown: false,
      addShown: false
    };
  }
  toogleQA = () => {
    this.setState(state => ({
      QAshown: !state.QAshown
    }));
  };
  addQA = () => {
    this.setState(state => ({
      addShown: !state.addShown
    }));
  };

  render() {
    return (
      <>
        <button onClick={this.addQA}>add a QA</button>
        <button onClick={this.toogleQA}> show/hide QA </button>
        <div
          style={{
            visibility: this.state.QAshown ? "visible" : "hidden"
          }}
        >
          <QA courseName={this.props.courseName} />
        </div>
        <addQA />
      </>
    );
  }
}
