import React from "react";
import FavorBtn from "./addFavor";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import MobileStepper from "@material-ui/core/MobileStepper";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import KeyboardArrowLeft from "@material-ui/icons/KeyboardArrowLeft";
import KeyboardArrowRight from "@material-ui/icons/KeyboardArrowRight";

const useStyles = makeStyles(theme => ({
  root: {
    Width: 400,
    height: 500
  },
  header: {
    display: "flex",
    alignItems: "center",
    height: 50,
    Width: "100%",
    // paddingLeft: theme.spacing(4),
    backgroundColor: theme.palette.background.default
  },
  contents: {
    height: 255,
    overflow: "hidden",
    display: "block",
    width: "100%"
  }
}));

export default function TextMobileStepper(props) {
  const classes = useStyles();
  const theme = useTheme();
  const path = String("../json/" + props.courseName + ".json");
  console.log(path);
  let tutorialSteps = require("../json/React.json");
  if (props.courseName != "React") tutorialSteps = require(path);
  // switch (props.courseName) {
  //   case "React":
  //     tutorialSteps = require("../json/React.json");
  //     break;
  //   case "HTML":
  //     tutorialSteps = require("../json/HTML.json");
  //     break;
  //   case "Java Script":
  //     tutorialSteps = require("../json/Java Script.json");
  //     break;
  //   case "Redux":
  //     tutorialSteps = require("../json/Redux.json");
  //     break;
  //   case "CSS":
  //     tutorialSteps = require("../json/CSS.json");
  //     break;
  //   case "Git":
  //     tutorialSteps = require("../json/Git.json");
  //     break;
  // }

  const [activeStep, setActiveStep] = React.useState(0);
  const maxSteps = tutorialSteps.length;

  function handleNext() {
    setActiveStep(prevActiveStep => prevActiveStep + 1);
  }

  function handleBack() {
    setActiveStep(prevActiveStep => prevActiveStep - 1);
  }

  return (
    <div style={{ Width: 400, height: 200 }}>
      <Paper square elevation={0} className={classes.header}>
        <Typography>
          <FavorBtn
            question={tutorialSteps[activeStep].label}
            answer={tutorialSteps[activeStep].answer}
            btnName="favor"
          />
          {tutorialSteps[activeStep].label}
        </Typography>
      </Paper>
      <div
        className={classes.contents}
        style={{
          display: "flex",
          justifyContent: "space-between",
          height: "100%",
          Width: 400
        }}
      >
        <input style={{ background: "blue" }}></input>
        <div>{tutorialSteps[activeStep].answer}</div>
      </div>
      <MobileStepper
        steps={maxSteps}
        position="static"
        variant="text"
        activeStep={activeStep}
        nextButton={
          <Button
            size="small"
            onClick={handleNext}
            disabled={activeStep === maxSteps - 1}
          >
            Next
            {theme.direction === "rtl" ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
            {theme.direction === "rtl" ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            Back
          </Button>
        }
      />
    </div>
  );
}
