import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import BottomNavigation from "@material-ui/core/BottomNavigation";
import BottomNavigationAction from "@material-ui/core/BottomNavigationAction";
import RestoreIcon from "@material-ui/icons/Restore";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Gui from "./gui";
import FolderIcon from "@material-ui/icons/Folder";
const useStyles = makeStyles({
  root: {
    width: 1150
  }
});

function SimpleBottomNavigation(props) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  return (
    <BottomNavigation
      value={value}
      onChange={(event, newValue) => {
        setValue(newValue);
      }}
      showLabels
      className={classes.root}
    >
      <BottomNavigationAction
        label="Recents"
        icon={<RestoreIcon />}
        onClick={() => {
          props.showDetail("Recents");
        }}
      />
      <BottomNavigationAction
        label="Favorites"
        icon={<FavoriteIcon />}
        onClick={() => {
          props.showDetail("Favorites");
        }}
      />
      <BottomNavigationAction
        label="Courses"
        icon={<FolderIcon />}
        onClick={() => {
          props.showDetail("Courses");
        }}
      />
    </BottomNavigation>
  );
}

const courseData = require("../json/course.json");

export default class MainGuide extends React.Component {
  constructor() {
    super();
    this.state = {
      view: ""
    };
  }

  showDetail = text => {
    this.setState({ view: text });
    const temp = <div>init</div>;
    switch (text) {
      case "Courses":
        console.log(text);
        temp = <div>course</div>;
        return;
      case "Recents":
        console.log(text);
        temp = <div>course</div>;
        return;
      case "Favorites":
        console.log(text);
        temp = <div>course</div>;
        return;
    }
  };

  render() {
    return (
      <div>
        <SimpleBottomNavigation showDetail={this.showDetail} />
        <Gui name={this.view} data={courseData} />
        <div>{temp}</div>
      </div>
    );
  }
}
