import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import BottomNavigation from "@material-ui/core/BottomNavigation";
import BottomNavigationAction from "@material-ui/core/BottomNavigationAction";
import RestoreIcon from "@material-ui/icons/Restore";
import FavoriteIcon from "@material-ui/icons/Favorite";
import Gui from "./gui";
import FolderIcon from "@material-ui/icons/Folder";
const useStyles = makeStyles({
  root: {
    width: 1150
  }
});

function SimpleBottomNavigation(props) {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  return (
    <BottomNavigation
      value={value}
      onChange={(event, newValue) => {
        setValue(newValue);
      }}
      showLabels
      className={classes.root}
    >
      <BottomNavigationAction
        label="Recents"
        icon={<RestoreIcon />}
        onClick={props.showDetail}
      />
      <BottomNavigationAction
        label="Favorites"
        icon={<FavoriteIcon />}
        onClick={props.showDetail}
      />
      <BottomNavigationAction
        label="Courses"
        icon={<FolderIcon />}
        onClick={props.showDetail}
      />
    </BottomNavigation>
  );
}

const courses = ["Java Script", "React", "Redux", "Github"];
const courseDetail = [
  "input java script notes here",
  "today I learned react",
  "Redux is not easy",
  "I like Github very much"
];

export default class MainGuide extends React.Component {
  constructor() {
    super();
    this.state = {
      btnType: ["course", "favor", "recent"],
      GuiType: ["course", "favor", "recent"]
    };
  }

  showDetail = e => {
    console.log(e.currentTarget.children[0].children[1].children);
  };

  render() {
    return (
      <div>
        <SimpleBottomNavigation showDetail={this.showDetail} />
        <Gui title={courses} detail={courseDetail} name={"courses"} />
      </div>
    );
  }
}
