import React, { Component } from "react";
import { BrowserRouter, Link, Route, Switch, Redirect } from "react-router-dom";

function GuideBar() {
  return (
    <div>
      {/* 编写路由导航 */}
      <ul>
        <li>
          <Link to="/">Home Page</Link>
        </li>
        <li>
          <Link to="/classes">My Courses</Link>
        </li>
        <li>
          <Link to="/mine">My Account</Link>
        </li>
        <li>
          <Link to="/mineaaaa">Not Found</Link>
        </li>
      </ul>

      <Switch>
        <Route exact path="/" component={Home}></Route>
        <Route path="/classes" component={Classes}></Route>
        <Route path="/login" component={Login}></Route>
        <RouteGuard path="/mine" component={Mine}></RouteGuard>
        <Route path="/detail/:course" component={Detail}></Route>
        <Route component={Notfound}></Route>
      </Switch>
    </div>
  );
}

class RouteGuard extends Component {
  render() {
    const { component: Component, ...otherProps } = this.props;
    return (
      <Route
        {...otherProps}
        render={props =>
          auth.isLogin ? (
            <Component {...props}></Component>
          ) : (
            <Redirect
              to={{
                pathname: "/login",
                state: { from: props.location.pathname }
              }}
            ></Redirect>
          )
        }
      ></Route>
    );
  }
}

class Login extends Component {
  state = {
    isLogin: false
  };
  login = () => {
    auth.login(() => {
      this.setState({
        isLogin: true
      });
    });
  };
  render() {
    const from =
      (this.props.location.state && this.props.location.state.from) || "/";
    if (this.state.isLogin) {
      return <Redirect to={from}></Redirect>;
    }
    return (
      <div>
        <p>Please Login Fist!</p>
        <button onClick={this.login}>Login</button>
      </div>
    );
  }
}
//模拟接口
const auth = {
  isLogin: false,
  login(callback) {
    this.isLogin = true;
    setTimeout(callback, 1000);
  }
};
//详情页组件
function Detail({ match, location, history }) {
  //没解构的写法是直接获取参数
  // console.log(props)
  console.log(match, location, history);
  console.log(location, "location");
  return (
    <div>
      我是{match.params.course}课程详情页
      <button
        onClick={() => history.push({ pathname: "/", state: { foo: "bar" } })}
      >
        返回首页
      </button>
    </div>
  );
}
//首页组件
function Home({ location }) {
  if (location.state && location.state.foo) {
    console.log("路由跳转带回来的参数：" + location.state.foo);
  }
  return (
    <div>
      首页
      {location.state && location.state.foo ? (
        <p>从详情页带回来的参数{location.state.foo}</p>
      ) : null}
    </div>
  );
}
//课程组件
function Classes() {
  return (
    <div>
      <ul>
        <li>
          <Link to="/detail/react">react</Link>
        </li>
        <li>
          <Link to="/detail/vue">vue</Link>
        </li>
      </ul>
    </div>
  );
}
//我的组件
function Mine() {
  return (
    <div>
      <h2>个人中心</h2>
      <ul>
        <li>
          <Link to="/mine/userinfo">个人信息</Link>
        </li>
        <li>
          <Link to="/mine/order">客户订单</Link>
        </li>
      </ul>
      {/* 路由配置 */}
      <Switch>
        <Route
          path="/mine/userinfo"
          component={() => <div>个人信息</div>}
        ></Route>
        <Route path="/mine/order" component={() => <div>客户订单</div>}></Route>
        {/* 路由重定向，当进入个人中心页面的时候都没有命中我们的路由配置的时候就显示重定向路由的内容 */}
        <Redirect to="/mine/userinfo"></Redirect>
      </Switch>
    </div>
  );
}
//配置404组件
function Notfound() {
  return <div>404页面</div>;
}

export default function RouterSample() {
  return (
    <div>
      <h1>演示react-router4.x版本的使用</h1>
      <BrowserRouter>
        <GuideBar />
      </BrowserRouter>
    </div>
  );
}
